import React, { Component } from 'react';
import MediaQuery from 'react-responsive';
import ScrollMenu from 'react-horizontal-scrolling-menu';

class MobileAbout extends Component{
    render() {
  return (
  		<MediaQuery className="mobileSection" query="(max-device-width: 800px)">

  		<div className="mobileBackground">

  		<div className="mobileSkills">

		<div className="mobileSub"><i class="fas fa-laptop-code fa-3x"></i><span className="skillsSpan">UI/UX</span></div>
		<div className="mobileSub"><i class="far fa-address-card"></i><span className="skillsSpan">Responsive Design</span></div>
		<div className="mobileSub"><i class="far fa-handshake"></i><span className="skillsSpan">Team Player</span></div>
		<div className="mobileSub"><i class="fas fa-running"></i><span className="skillsSpan">Versatile</span></div>

		</div>

		</div>

  		</MediaQuery>

    );
  }
}

export default MobileAbout;
