                        <a href="#">Link</a>
                        <a href="#">Link</a>
                    </Navigation>
                </Drawer>
                <Content />
            </Layout>
          </div>
      </Headroom>
  	</MediaQuery>

    );
  }
}

export default MobileNavigation;
